import React, { useEffect, useState } from 'react';
import {Link} from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux';
import { detailsUser } from '../actions/UserActions';


function SigninScreen (props) {

    const [qty, setQty] = useState(1);
    const UserDetails = useSelector(state => state.UserDetails);
    const { User, loading, error } = UserDetails;
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(detailsUser(props.match.params.id));
        return () => {
            //
        };
    },  [])

    const handleAddToCart = () => {
        props.history.push("/cart/" + props.match.params.id + "?qty=" + qty)
    }

    return <div>
        <div className="back-to-result">
            <Link to="/">Back to result</Link>
        </div>
        {loading? <div>Loading...</div>:
        error? <div>{error}</div>:
        (
            <div className="details">
            <div className="details-image">
                <img src={User.image} alt="User" ></img>
            </div>
            <div className="details-info">
                <ul>
                    <li>
                        <h4>{User.name}</h4>
                    </li>
                    <li>
                        {User.rating} Stars ({User.numReviews} Reviews)
                    </li>
                    <li>
                        Price: <b>${User.price}</b>
                    </li>
                    <li>
                        Description:
                        <div>
                            {User.description}
                        </div>
                    </li>
                </ul>
            </div>
            <div className="details-action">
                <ul>
                    <li>
                        Price: <b>${User.price}</b>
                    </li>
                </ul>
                <ul>
                    <li>
                        Status: {User.countInStock>0? "In Stock" : "Out of Stock"}
                    </li>
                </ul>
                <ul>
                    <li>
                        Qty: <select value ={qty} onChange={(e) => {setQty(e.target.value)}}>
                            {[...Array(User.countInStock).keys()].map(x =>
                                <option key={x+1} value={x+1}>{x+1}</option>
                                
                                
                                )}
                        </select>
                    </li>
                    <li>
                        {User.countInStock>0 && <button onClick={handleAddToCart} className="button primary" >Add to Cart</button>

                            }
                    </li>
                </ul>
            </div>          
           
        </div>


        )

    }          

    
    </div>

}
        
export default SigninScreen;
